(2999, 285, '_home_who_we_section_2_home_who_we_section_header', 'field_65e0a35862957'),
(3000, 285, 'home_who_we_section_2_home_who_we_section_content', 'our comprehensive range of services covers every aspect of facade and building solutions. Whether you require intricate facade services like Cladding, External finish, Formwork, fixture, fitting of service installation, or seamless building construction to renovations, our expert team is poised to turn your dreams into reality. With expertise in structural reengineering, consultations, and building and renovation services in the Western province, we are committed to expanding our services island wide to serve you better.'),
(3001, 285, '_home_who_we_section_2_home_who_we_section_content', 'field_65e0a37e62958'),
(3002, 285, 'home_who_we_section_3_home_who_we_section_header', ' Your Trusted Advisors and Partners'),
(3003, 285, '_home_who_we_section_3_home_who_we_section_header', 'field_65e0a35862957'),
